import java.util.*;

/*
 * 0581-shortest-unsorted-continuous-subarray.java
 *
 * @author Yuvv <yuvv_th@outlook.com>
 * @date 2023/01/01
 */
public class Solution {
    public int findUnsortedSubarray(int[] nums) {
        int i = nums.length + 1, j = nums.length;
        LinkedList<Integer> stack = new LinkedList<>();
        stack.push(nums[0]);
        for (int k = 1; k < nums.length; k++) {
            if (nums[k] >= stack.peek()) {
                stack.push(nums[k]);
            } else {
                j = k;
                int max = stack.peek();
                while (!stack.isEmpty() && stack.peek() > nums[k]) {
                    stack.pop();
                }
                i = Math.min(i, stack.size());
                stack.push(nums[k]);
                stack.push(max);  // we only need to push back the max value
            }
        }
        return j - i + 1;
    }

    public static void main(String[] args) {
        Solution s = new Solution();
        // 5
        System.out.println(s.findUnsortedSubarray(
            new int[] {2,6,4,8,10,9,15}
        ));
        // 0
        System.out.println(s.findUnsortedSubarray(
            new int[] {1,2,3,4}
        ));
    }
}

